define(['uiComponent','uiRegistry','jquery'],function(Component,registry,$){'use strict';return Component.extend({initialize:function(options,element){this.button=$(element);this.search=$('.block-search');this.background=$('.search-background');this.setupClickListeners();registry.set('search-toggle',this);},toggle:function(){this.search.add(this.button).toggleClass('show');this.search.find('input').focus();},setupClickListeners:function(){var self=this;this.background.on('click',function(){self.toggle();});}});});